require_once "classes/conexao.php";
require_once "model/cidade.php";
require_once "model/usuario.php";

class CidadeController extends CidadeModel {
 
	public static function BuscarTodosEstado()
	{
		$controller = ucfirst($_GET['page'])."Controller";
		$tabela = $controller::$tabela;
		$id = $_GET['estado'];

		$sql = "SELECT * FROM $tabela WHERE tb_estado_id = $id";
		$dados = parent::executar($sql, 'all');

		if(!empty($dados)){
			return array(
				"status" => 200,
				"data" => $dados
			);
		} else {
			return array(
				"status" => 200,
				"data" => "Nada encontrado"
			);
		}
	}

	public static function getCidadesCadastradas(){
		$sql = "SELECT tb_cidade.id, concat(tb_estado.sigla, ' - ' ,tb_cidade.nome) as cidade FROM tb_cidade, tb_endereco, tb_estado
				WHERE tb_endereco.tb_cidade_id = tb_cidade.id and tb_cidade.tb_estado_id = tb_estado.id
				GROUP BY tb_cidade.id";
		$op = Conexao::getInstance()->prepare($sql);
		$op->execute();
		$dados = (object) $op->fetchall(PDO::FETCH_ASSOC);
		return array(
			"status" => 200,
			"data" => $dados
		);
	}

}

